function startSlider(sliderItem,countSteps=0,vertical=false,marginBetweenItems=0,responsiveArray=null){const slider=document.querySelector(sliderItem);const navSliderItems=slider.getElementsByClassName('slider__item');const contentList=slider.children[1];const contentItems=contentList.querySelectorAll('li');let currentContentItem=contentItems[countSteps];if(responsiveArray){let breakpointIndex=-1;responsiveArray.forEach(({breakpoint}=item,index)=>{if(breakpoint>=window.innerWidth){breakpointIndex=index}});if(breakpointIndex===-1){initSlider(slider,countSteps,navSliderItems,vertical,marginBetweenItems,contentList,currentContentItem,contentItems)}else{const{settings:{countSteps:setCountSteps=0,vertical:setVertical=false,marginBetweenItems:setMarginBetweenItems=0,},}=responsiveArray[breakpointIndex];startSlider(sliderItem,setCountSteps,setVertical,setMarginBetweenItems);return}}else{initSlider(slider,countSteps,navSliderItems,vertical,marginBetweenItems,contentList,currentContentItem,contentItems)}slider.addEventListener('click',function(e){const button=e.target.closest('button');const arrayButtons=[...navSliderItems[0].parentElement.querySelectorAll('button'),];if(button.classList.contains('slider__button')){e.target.closest('button').classList.contains('prev')?(()=>{if(countSteps!==0){button.nextElementSibling.removeAttribute('disabled');--countSteps;navSliderItems[countSteps].classList.toggle('slider__item--active');navSliderItems[countSteps+1].classList.toggle('slider__item--active');contentList.style.transform=getTransformSliderContent(countSteps+1,countSteps,contentList,contentItems,vertical,marginBetweenItems)[1]}})():(()=>{if(navSliderItems.length-1>countSteps){button.previousElementSibling.removeAttribute('disabled');++countSteps;navSliderItems[countSteps].classList.toggle('slider__item--active');navSliderItems[countSteps-1].classList.toggle('slider__item--active');contentList.style.transform=getTransformSliderContent(countSteps-1,countSteps,contentList,contentItems,vertical,marginBetweenItems)[1]}})();if(countSteps===0){button.setAttribute('disabled','')}if(navSliderItems.length-1===countSteps){button.setAttribute('disabled','')}}if(button.closest('.slider__item')){button.closest('.slider__wrapper').children[1].removeAttribute('disabled');button.closest('.slider__wrapper').children[2].removeAttribute('disabled');contentList.style.transform=getTransformSliderContent(countSteps,arrayButtons.indexOf(button),contentList,contentItems,vertical,marginBetweenItems)[1];navSliderItems[countSteps].classList.toggle('slider__item--active');countSteps=arrayButtons.indexOf(button);navSliderItems[countSteps].classList.toggle('slider__item--active');if(countSteps===0){button.closest('.slider__wrapper').children[1].setAttribute('disabled','')}if(navSliderItems.length-1===countSteps){button.closest('.slider__wrapper').children[2].setAttribute('disabled','')}}currentContentItem=contentItems[countSteps];if(vertical===true){slider.style.maxHeight=`${currentContentItem.offsetHeight}px`}else{contentList.style.maxWidth=`${currentContentItem.offsetWidth}px`}})}function initSlider(slider,countSteps,navSliderItems,vertical,marginBetweenItems,contentList,currentContentItem,contentItems){slider.style.overflow='hidden';contentList.style.willChange='transform';contentList.style.transform='translate(0)';navSliderItems[countSteps].classList.toggle('slider__item--active');contentList.style.transform=getTransformSliderContent(0,countSteps,contentList,contentItems,vertical,marginBetweenItems)[1];if(vertical===true){slider.style.maxHeight=`${currentContentItem.offsetHeight}px`;contentItems.forEach((item)=>{item.style.marginBottom=`${item.offsetHeight*(marginBetweenItems/100)}px`})}else{contentList.style.width=`${currentContentItem.offsetWidth}px`;contentList.style.display='flex';contentItems.forEach((item)=>{item.style.marginRight=`${marginBetweenItems}%`;item.style.minWidth='100%'})}}function getTransformSliderContent(countStepsPrev,countStepsNext,contentList,contentItems,vertical,marginBetweenItems){const currentTranslateValue=+contentList.style.transform.match(/[-.0-9]+(?=px)|0/)[0];let accumulateTranslate=0;if(countStepsPrev<countStepsNext){for(let index=countStepsPrev;index<countStepsNext;++index){vertical?(accumulateTranslate-=contentItems[index].offsetHeight+(marginBetweenItems/100)*contentItems[index].offsetHeight):((accumulateTranslate-=(contentItems[index].offsetWidth+(marginBetweenItems/100)*contentItems[index].offsetWidth)*(countStepsNext-countStepsPrev)),(index=countStepsNext))}}else{for(let index=countStepsPrev;countStepsNext<index;--index){vertical?(accumulateTranslate+=contentItems[index-1].offsetHeight+(marginBetweenItems/100)*contentItems[index-1].offsetHeight):((accumulateTranslate+=(contentItems[index-1].offsetWidth+(marginBetweenItems/100)*contentItems[index-1].offsetWidth)*(countStepsPrev-countStepsNext)),(index=countStepsNext))}}return[accumulateTranslate,vertical?`translateY(${accumulateTranslate+currentTranslateValue}px)`:`translateX(${accumulateTranslate+currentTranslateValue}px)`,currentTranslateValue,]}